import React, { useContext } from "react";
import { ForecastContext } from "./context/ForecastContext";

function Today() {
  const { cityDisplay, weatherData, dayName } = useContext(ForecastContext);
  const urlBase = "";
  return (
    <div>
      {weatherData.map((item, i) => {
        return (
          <div key={i}>
            {i === 0 ? (
              <div className="today forecast">
                <div className="forecast-header">
                  <div className="day">{dayName}</div>
                  <div className="date">6 Oct</div>
                </div>
                <div className="forecast-content">
                  <div className="location">{cityDisplay}</div>
                  <div className="degree">
                    <div className="num">
                      Min: {item.minTemp} - Max: {item.maxTemp}
                      <sup>o</sup>C
                    </div>
                    <div className="forecast-icon">
                      <img
                        src={`https://www.weatherbit.io/static/img/icons/${item.iconCode}.png`}
                        alt=""
                        width="90"
                      />
                    </div>
                  </div>
                  <span>
                    <img src="images/icon-umberella.png" alt="" />
                    20%
                  </span>
                  <span>
                    <img src="images/icon-wind.png" alt="" />
                    18km/h
                  </span>
                  <span>
                    <img src="images/icon-compass.png" alt="" />
                    East
                  </span>
                </div>
              </div>
            ) : (
              <div key={i}>
                <h1>No: {i} - hahahahaha çalıştı</h1>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default Today;
