import { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import CitySelector from "./components/CitySelector";
import SevenDays from "./components/SevenDays";
import Today from "./components/Today";

const APIKEY = process.env.REACT_APP_API_KEY;

function App() {
  const [lang, setLang] = useState("tr");
  const [localSuf, setLocalSuf] = useState("tr-TR");
  const [city, setCity] = useState("istanbul");
  const [weatherData, setWeatherData] = useState();

  const langHandler = () => {
    if (lang === "tr") {
      setLang("en");
      setLocalSuf("en-EN");
    } else if (lang === "en") {
      setLang("tr");
      setLocalSuf("tr-TR");
    }
  };
  const langText = lang.toUpperCase();

  useEffect(() => {
    const getData = async () => {
      try {
        const wData = await axios.get(
          `https://api.weatherbit.io/v2.0/forecast/daily?city=${city}&country=turkey&days=8&units=C&lang=${lang}&key=${APIKEY}`
        );
        setWeatherData(wData.data);
        console.log(weatherData);
        // .then((resp) => setWeatherData(resp))
        // .catch((err) => console.log(err));
      } catch (e) {
        console.log(e);
      }
    };

    getData();
  }, [city]);

  const todayData = weatherData;

  const myDate = new Date();
  // console.log(
  //   "This is it :",
  //   myDate.toLocaleString(
  //     { localSuf },
  //     {
  //       weekday: "long",
  //     }
  //   )
  // );
  return (
    <div className="App">
      <h1>Weather App</h1>
      <button onClick={langHandler}>{langText}</button>
      <form>
        <label htmlFor="cities">City: </label>
        <CitySelector city={city} setCity={setCity} />
        {/* <Today todayData={todayData} />
        <SevenDays sevenDaysData={sevenDaysData} /> */}
      </form>
    </div>
  );
}

export default App;
