import axios from "axios";

export const getData = async (city, lang, APIKEY) => {
  try {
    const theData = await axios.get(
      `https://api.weatherbit.io/v2.0/forecast/daily?city=${city}&country=turkey&days=8&units=C&lang=${lang}&key=${APIKEY}`
    );
    console.log(theData.data);
    const resArray = theData.data.data.map((item) => {
      const dayNamesTr = [
        "Pazartesi",
        "Salı",
        "Çarşamba",
        "Perşembe",
        "Cuma",
        "Cumartesi",
        "Pazar",
      ];
      const dayNamesEn = [
        "Monday",
        "Tuesday",
        "Wednesday",
        "Thursday",
        "Friday",
        "Saturday",
        "Sunday",
      ];
      return {
        maxTemp: item.app_max_temp,
        minTemp: item.app_min_temp,
        dateTime: "Nothing",
        iconCode: item.weather.icon,
      };
    });
    <li>
      <h1 style={{ color: "purple" }}>
        This is the test {resArray["max-temp"]}
      </h1>
    </li>;
    console.log("resArray", resArray);
    return resArray;
  } catch (e) {
    console.log(e);
  }
};
