import { useState, useEffect } from "react";
import "./App.css";
import CitySelector from "./components/CitySelector";
import SevenDays from "./components/SevenDays";
import Today from "./components/Today";
import { getData } from "./components/Test";
import Forecast from "./components/Forecast";
import OneDay from "./components/OneDay";
import ForecastProvider from "./components/context/ForecastContext";

const APIKEY = process.env.REACT_APP_API_KEY;

function App() {
  const [lang, setLang] = useState("tr");
  const [localSuf, setLocalSuf] = useState("tr-TR");
  const [city, setCity] = useState("istanbul");
  const [weatherData, setWeatherData] = useState();

  const langHandler = () => {
    if (lang === "tr") {
      setLang("en");
      setLocalSuf("en-EN");
    } else if (lang === "en") {
      setLang("tr");
      setLocalSuf("tr-TR");
    }
  };

  const langText = lang.toUpperCase();

  useEffect(() => {
    getData(city, lang, APIKEY);
  }, [city, lang]);

  const todayData = weatherData;

  const myDate = new Date();
  console.log(
    "This is it :",
    myDate.toLocaleString(
      { localSuf },
      {
        weekday: "long",
      }
    )
  );

  return (
    <ForecastProvider>
      <div className="App">
        <h1>Weather App</h1>
        <button onClick={langHandler}>{langText}</button>
        <form>
          <label htmlFor="cities">City: </label>
          <CitySelector city={city} setCity={setCity} />
          {/* <Today todayData={todayData} />
        <SevenDays sevenDaysData={sevenDaysData} /> */}
        </form>
        <Forecast>
          <Today city={city} />
          <SevenDays>
            <OneDay />
            <OneDay />
            <OneDay />
            <OneDay />
            <OneDay />
            <OneDay />
            <OneDay />
          </SevenDays>
        </Forecast>
      </div>
    </ForecastProvider>
  );
}

export default App;
