import { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import CitySelector from "./components/CitySelector";

function App() {
  const [city, setCity] = useState("istanbul");

  useEffect(() => {
    axios
      .get(
        "https://api.weatherbit.io/v2.0/forecast/daily?city=izmir&country=turkey&days=8&units=S&lang=tr&key=97099c9357ad424ab2590532b08b2338"
      )
      .then((res) => console.log(res.data.city_name))
      .catch((err) => console.log(err));
  }, [city]);

  return (
    <div className="App">
      <h1>Weather App</h1>
      <form>
        <label htmlFor="cities">City: </label>
        <CitySelector city={city} setCity={setCity} />
      </form>
    </div>
  );
}

export default App;
