import { createContext, useState, useEffect } from "react";
import { getData } from "../Test";
export const ForecastContext = createContext();

export const ForecastProvider = ({ children }) => {
  const [lang, setLang] = useState("tr");
  const [localSuf, setLocalSuf] = useState("tr-TR");
  const [city, setCity] = useState("istanbul");
  const [weatherData, setWeatherData] = useState([]);

  const APIKEY = process.env.REACT_APP_API_KEY;

  const langHandler = () => {
    if (lang === "tr") {
      setLang("en");
      setLocalSuf("en-EN");
    } else if (lang === "en") {
      setLang("tr");
      setLocalSuf("tr-TR");
    }
  };

  const todayData = weatherData;

  const myDate = new Date();
  console.log(
    "This is it :",
    myDate.toLocaleString(
      { localSuf },
      {
        weekday: "long",
      }
    )
  );

  const langText = lang.toUpperCase();

  const values = {
    lang,
    setLang,
    localSuf,
    setLocalSuf,
    city,
    setCity,
    weatherData,
    setWeatherData,
    langHandler,
    langText,
    todayData,
    getData,
  };

  useEffect(() => {
    getData(city, lang, APIKEY).then((response) => {
      setWeatherData(response);
    });
  }, [city, lang]);
  console.log("iştebu:", weatherData);

  return (
    <ForecastContext.Provider value={values}>
      {children}
    </ForecastContext.Provider>
  );
};

export default ForecastContext;
